import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button, Alert, Form } from 'react-bootstrap';
import { useCart } from '../CartContext/CartContext';
import { Link, useLocation } from 'react-router-dom';
import './Catalog.css';

function Catalog() {
    const location = useLocation();
    const isHomePage = location.pathname === '/';
    const { addToCart } = useCart();
    const [products, setProducts] = useState([]);
    const [addedToCart, setAddedToCart] = useState(false);
    const [error, setError] = useState(null);
    const [selectedCategory, setSelectedCategory] = useState(localStorage.getItem('selectedCategory') || 'all');
    const [currentPage, setCurrentPage] = useState(parseInt(localStorage.getItem('currentPage')) || 1);
    const itemsPerPage = isHomePage ? 3 : 10; // Например, на главной 3, в каталоге 10

    useEffect(() => {
        const fetchProducts = async () => {
            setError(null);
            try {
                const queryParams = new URLSearchParams();
                queryParams.append('category', selectedCategory);
                queryParams.append('page', currentPage);
                queryParams.append('limit', itemsPerPage);

                const response = await fetch(`/api/products?${queryParams}`);
                if (!response.ok) {
                    throw new Error(`HTTP error: status ${response.status}`);
                }
                const data = await response.json();
                setProducts(data);
            } catch (error) {
                console.error('Error fetching products:', error);
                setError(error.message);
            }
        };

        fetchProducts();
        // Сохранение выбранных фильтров и страницы в localStorage
        localStorage.setItem('selectedCategory', selectedCategory);
        localStorage.setItem('currentPage', currentPage);
    }, [selectedCategory, currentPage, itemsPerPage]);

    const handleAddToCart = (product) => {
        addToCart(product);
        setAddedToCart(true);
        setTimeout(() => setAddedToCart(false), 2000);
    };

    const handleChangeCategory = (e) => {
        setSelectedCategory(e.target.value);
        setCurrentPage(1); // Сброс к первой странице при смене категории
    };

    const totalPages = Math.ceil(products.length / itemsPerPage);
    const displayedProducts = products.slice((currentPage - 1) * itemsPerPage, currentPage * itemsPerPage);

    return (
        <Container className="catalog-container my-5">
            {addedToCart && <Alert variant="success">Product added to cart!</Alert>}
            {error && <Alert variant="danger">Error: {error}</Alert>}
            {/* Оставшийся код компонента не изменился */}
        </Container>
    );
}

export default Catalog;
