import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button, Alert, Form } from 'react-bootstrap';
import { useCart } from '../CartContext/CartContext';
import { Link, useLocation } from 'react-router-dom';
import './Catalog.css';

function Catalog() {
    const location = useLocation();
    const isHomePage = location.pathname === '/';
    const { addToCart } = useCart();
    const [products, setProducts] = useState([]);
    const [addedToCart, setAddedToCart] = useState(false);
    const [error, setError] = useState(null);
    const [selectedCategory, setSelectedCategory] = useState('all');
    const [currentPage, setCurrentPage] = useState(1);
    const itemsPerPage = isHomePage ? 3 : 3;

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const queryParams = new URLSearchParams();
                if (!isHomePage) {
                    queryParams.append('category', selectedCategory);
                    queryParams.append('page', currentPage);
                    queryParams.append('limit', itemsPerPage);
                }

                const token = localStorage.getItem('token');
                const response = await fetch(`/api/products?${queryParams}`, {
                    headers: token ? { 'Authorization': `Bearer ${token}` } : {},
                });

                if (!response.ok) {
                    throw new Error(`HTTP error: status ${response.status}`);
                }
                const data = await response.json();
                setProducts(data);
            } catch (error) {
                console.error('Error fetching products:', error);
                setError(error.message);
            }
        };

        fetchProducts();
    }, [isHomePage, selectedCategory, currentPage, itemsPerPage]);

    const handleAddToCart = (product) => {
        addToCart(product);
        setAddedToCart(true);
        setTimeout(() => setAddedToCart(false), 2000);
    };

    const handleChangeCategory = (e) => {
        setSelectedCategory(e.target.value);
        setCurrentPage(1); // Reset to first page when category changes
    };

    const totalPages = Math.ceil(products.length / itemsPerPage);
    const displayedProducts = products.slice((currentPage - 1) * itemsPerPage, currentPage * itemsPerPage);

    if (error) {
        return <Alert variant="danger">Error: {error}</Alert>;
    }

    return (
        <Container className="catalog-container my-5">
            {addedToCart && <Alert variant="success">Product added to cart!</Alert>}
            <Row>
                <Col xs={12} className="text-center mb-4">
                    <h2>Catalog of Products</h2>
                    <Form.Select aria-label="Select category" className='select-form' onChange={handleChangeCategory} value={selectedCategory}>
                        <option value="all">All</option>
                        <option value="beds">Beds</option>
                        <option value="kitchen">Kitchen</option>
                        {/* Add more options based on your categories */}
                    </Form.Select>
                </Col>
            </Row>
            <Row className="justify-content-center">
                {displayedProducts.map(product => (
                    <Col key={product.id} xs={12} sm={6} md={4} lg={3} className="mb-4 d-flex align-items-stretch">
                        <Card className="product-card">
                            <Card.Img variant="top" src={product.image} alt={product.title} />
                            <Card.Body className="d-flex flex-column">
                                <Card.Title>{product.title}</Card.Title>
                                <Card.Text className="mt-auto">{`${product.price} RUB`}</Card.Text>
                                <Button variant="primary" className="mt-auto" onClick={() => handleAddToCart(product)}>Add to Cart</Button>
                            </Card.Body>
                        </Card>
                    </Col>
                ))}
            </Row>
            {!isHomePage && (
                <Row className="justify-content-center">
                    {[...Array(totalPages).keys()].map(page => (
                        <Col key={page} xs="auto">
                            <Button variant={currentPage === page + 1 ? "dark" : "light"} onClick={() => setCurrentPage(page + 1)}>
                                {page + 1}
                            </Button>
                        </Col>
                    ))}
                </Row>
            )}
            {isHomePage && (
                <Row className="justify-content-center">
                    <Col xs={12} className="text-center">
                        <Button variant="outline-primary" className="show-more-button" as={Link} to="/catalog">See all products</Button>
                    </Col>
                </Row>
            )}
        </Container>
    );
}

export default Catalog;
