import React, { useState, useEffect } from 'react';
import './IntroSection.css';

function IntroSection() {
    const [introData, setIntroData] = useState({
        title: '',
        sections: [],
        images: []
    });
    const [error, setError] = useState(null);

    // Асинхронная функция для загрузки данных
    const fetchData = async () => {
        try {
            const response = await fetch('/api/introSection');
            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }
            const data = await response.json();
            setIntroData({
                title: data.title,
                sections: data.paragraphs.sections, // Обновите путь к данным
                images: data.images.images // Обновите путь к данным
            });
        } catch (error) {
            console.error('Error fetching data:', error);
            setError(error.message);
        }
    };

    useEffect(() => {
        fetchData();
    }, []);

    if (error) {
        return <div>Error loading Intro Section: {error}</div>;
    }

    // Отображение контента раздела
    return (
        <section className="intro-section">
             <h2>{introData.title || 'Портфолио'}</h2>
            {introData.sections.map((section, index) => (
                <div key={index} className='image-container'>
                    <h3>{section.header}</h3>
                </div>
            ))}

            <div className="gallery">
                {introData.images.map((img, index) => (
                    <img key={index} src={img} alt={`Изображение ${index + 1}`} />
                ))}
            </div>
        </section>
    );
}

export default IntroSection;
