import React from 'react';
import { useCart } from '../CartContext/CartContext';
import './PlaceOrder.css'; // Убедитесь, что путь к CSS-файлу верный

const PlaceOrderComponent = () => {
  const { placeOrder, orderStatus } = useCart();

  return (
    <div>
      <button className="place-order-button" onClick={placeOrder}>Оформить заказ</button>
      {orderStatus === 'success' && (
        <p className="order-success-message">Ваш заказ успешно оформлен!</p>
      )}
    </div>
  );
};

export default PlaceOrderComponent;
