import React, { useState, useEffect } from 'react';
import { Modal, Button } from 'react-bootstrap'; // Импорт компонентов Bootstrap
import './IntroSection.css';

function IntroSection() {
    const [introData, setIntroData] = useState({ title: '', sections: [], images: [] });
    const [selectedSectionIndex, setSelectedSectionIndex] = useState(null);
    const [error, setError] = useState(null);

    const fetchData = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch('/api/introSection', {
                headers: token ? { 'Authorization': `Bearer ${token}` } : {},
            });

            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }
            const data = await response.json();
            setIntroData({
                title: data.title,
                sections: data.paragraphs.sections,
                images: data.images.images
            });
        } catch (error) {
            console.error('Error fetching data:', error);
            setError(error.message);
        }
    };

    useEffect(() => {
        fetchData();
    }, []);

    const openModal = (index) => {
        setSelectedSectionIndex(index);
    };

    const closeModal = () => {
        setSelectedSectionIndex(null);
    };

    if (error) {
        return <div className="alert alert-danger">Error loading Intro Section: {error}</div>;
    }

    return (
        <section className="intro-section">
            <h2 className="text-center mb-4">{introData.title || 'О нашей компании'}</h2>
            <div className="gallery row">
                {introData.images.map((img, index) => (
                    <div key={index} className="col-md-4 col-sm-6 mb-3">
                        <div className="image-block" onClick={() => openModal(index)}>
                            <img src={img} alt={`Изображение ${index + 1}`} className="img-thumbnail" />
                        </div>
                    </div>
                ))}
            </div>

            <Modal show={selectedSectionIndex !== null} onHide={closeModal}>
                <Modal.Header closeButton>
                    <Modal.Title>{introData.sections[selectedSectionIndex]?.header}</Modal.Title>
                </Modal.Header>
                <Modal.Body>{introData.sections[selectedSectionIndex]?.content}</Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={closeModal}>Закрыть</Button>
                </Modal.Footer>
            </Modal>
        </section>
    );
}

export default IntroSection;
