import React, { useContext, useEffect } from 'react';
import { CartContext } from '../CartContext/CartContext';
import './PlaceOrder.css';

const PlaceOrderComponent = () => {
  const { placeOrder, orderStatus } = useContext(CartContext);
  const showModal = orderStatus === 'success';

  useEffect(() => {
    let timer;
    if (orderStatus === 'success') {
      timer = setTimeout(() => {
        // Reset order status after showing modal
      }, 3000);
    }
    return () => clearTimeout(timer);
  }, [orderStatus]);

  return (
    <div>
      <button className="place-order-button" onClick={placeOrder}>Оформить заказ</button>
      {showModal && (
        <div className="modal active">
          <div className="modal-header">Заказ Оформлен!</div>
          <p>Ваш заказ успешно оформлен и скоро будет обработан.</p>
        </div>
      )}
      {orderStatus === 'error' && (
        <p className="order-error-message">
          Произошла ошибка при оформлении заказа. Пожалуйста, попробуйте еще раз.
        </p>
      )}
    </div>
  );
};

export default PlaceOrderComponent;
