import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCalculator } from '@fortawesome/free-solid-svg-icons';
import { faTelegramPlane } from '@fortawesome/free-brands-svg-icons';
import './FloatingIcons.css';
import FurnitureConstructor from './FurnitureConstructor'; // Импортируем компонент конструктора

const FloatingIcons = () => {
  const [showConstructor, setShowConstructor] = useState(false);

  const handleCalculatorClick = () => {
    setShowConstructor(true);
  };

  const handleClose = () => {
    setShowConstructor(false);
  };

  return (
    <div className="floating-icons">
      <button className="icon" title="Расчет стоимости" onClick={handleCalculatorClick}>
        <FontAwesomeIcon icon={faCalculator} size="2x" />
      </button>
      <a href="https://t.me/yourusername" target="_blank" rel="noopener noreferrer" className="icon" title="Связь в Telegram">
        <FontAwesomeIcon icon={faTelegramPlane} size="2x" />
      </a>

      {showConstructor && (
        <div className="constructor-modal">
          <button onClick={handleClose}>Закрыть</button>
          <FurnitureConstructor />
        </div>
      )}
    </div>
  );
};

export default FloatingIcons;
