import React, { useState, useEffect } from 'react';
import { useAuth } from '../AuthContext/AuthContext';
import AuthForm from '../AuthForm/AuthForm';
import AdminPanel from './AdminPanel';
import ContentEditor from './ContentEditor';
import SiteStatistics from './SiteStatistics';
import "./AuthForm.css";

const UserProfile = () => {
  const { user, logout } = useAuth();
  const [users, setUsers] = useState([]);

  useEffect(() => {
    fetch('/api/users')
      .then(response => response.json())
      .then(data => setUsers(data))
      .catch(error => console.error('Error fetching users:', error));
  }, []);

  const handleEdit = (userId) => {
    // Здесь должна быть логика для редактирования пользователя
    console.log('Редактирование пользователя с ID:', userId);
  };

  const handleDelete = (userId) => {
    // Здесь должна быть логика для удаления пользователя
    console.log('Удаление пользователя с ID:', userId);
  };

  const renderAdminContent = () => (
    <div className="admin-content">
      <h2>Панель администратора</h2>
      <AdminPanel users={users} onEdit={handleEdit} onDelete={handleDelete} />
      <ContentEditor />
      <SiteStatistics />
      <button onClick={logout} className="btn btn-danger">Выйти</button>
    </div>
  );

  const renderUserContent = () => (
    <div className="user-content">
      <h2>Привет, {user?.name}</h2>
      <h3>Ваши заказы:</h3>
      {/* Здесь могут быть компоненты или функционал, специфичный для обычного пользователя */}
      <button onClick={logout} className="btn btn-danger">Выйти</button>
    </div>
  );

  return (
    <div className="user-profile">
      <h1>Личный кабинет</h1>
      {!user ? <AuthForm /> : user.role === 'admin' ? renderAdminContent() : renderUserContent()}
    </div>
  );
};

export default UserProfile;
