import React, { createContext, useContext, useState, useEffect } from 'react';

export const AuthContext = createContext(null);

export const useAuth = () => useContext(AuthContext);

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      fetchUserData(token);
    }
  }, []);

  const fetchUserData = async (token) => {
    setIsLoading(true);
    try {
      const response = await fetch('/api/users/me', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        // Если ответ от сервера не OK, предполагаем, что токен недействителен
        localStorage.removeItem('token');
        setUser(null);
        setError('Ошибка аутентификации. Пожалуйста, войдите снова.');
        return;
      }

      const data = await response.json();
      setUser({ name: data.username, role: data.role });
    } catch (error) {
      setError('Ошибка при получении данных пользователя');
      setUser(null);
    } finally {
      setIsLoading(false);
    }
  };

  const handleAuthResponse = async (response) => {
    const data = await response.json();
    if (!response.ok) {
      if (response.status === 400) {
        throw new Error('Не валидные данные');
      } else if (response.status === 409) {
        throw new Error('Такой пользователь уже есть в системе');
      } else {
        throw new Error(data.message || 'Неверный логин или пароль');
      }
    }
    return data;
  };

  const login = async (username, password) => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch('/api/users/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, password }),
      });

      const data = await handleAuthResponse(response);
      setUser({ name: data.username, role: data.userRole });
      localStorage.setItem('token', data.token);
    } catch (error) {
      setError(error.message);
      setUser(null);
    } finally {
      setIsLoading(false);
    }
  };

  const register = async (username, password) => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch('/api/users/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, password }),
      });

      const data = await handleAuthResponse(response);
      setUser({ name: data.username, role: data.role });
      localStorage.setItem('token', data.token);
    } catch (error) {
      setError(error.message);
      setUser(null);
    } finally {
      setIsLoading(false);
    }
  };



  const logout = () => {
    setUser(null);
    localStorage.removeItem('token');
    setError(null);
  };

  return (
    <AuthContext.Provider value={{ user, isLoading, error, login, register, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

export default AuthContext;
