import React, { useState, useEffect } from 'react';
import { useCart } from '../CartContext/CartContext';
import './PlaceOrder.css';

const PlaceOrderComponent = () => {
  const { cartItems, placeOrder, orderStatus } = useCart();
  const [showModal, setShowModal] = useState(false);

  const handlePlaceOrder = () => {
    const token = localStorage.getItem('token');

    if (!token) {
      alert('Пожалуйста, войдите в систему для оформления заказа.');
      return;
    }

    if (cartItems.length > 0) {
      placeOrder();
    } else {
      alert('Ваша корзина пуста.');
    }
  };

  useEffect(() => {
    if (orderStatus === 'success') {
      setShowModal(true);
      setTimeout(() => setShowModal(false), 3000);
    }
  }, [orderStatus]);

  return (
    <div>
      <button className="place-order-button" onClick={handlePlaceOrder}>Оформить заказ</button>

      {showModal && (
        <div className="modal">
          <div className="modal-header">Заказ Оформлен!</div>
          <p>Ваш заказ успешно оформлен и скоро будет обработан.</p>
        </div>
      )}

      {orderStatus === 'error' && (
        <p className="order-error-message">
          Произошла ошибка при оформлении заказа. Пожалуйста, попробуйте еще раз.
        </p>
      )}
    </div>
  );
};

export default PlaceOrderComponent;
