import React, { useState, useEffect } from 'react';
import './IntroSection.css';

function IntroSection() {
    const [introData, setIntroData] = useState({ title: '', sections: [], images: [] });
    const [selectedBlock, setSelectedBlock] = useState(null);
    const [error, setError] = useState(null);

    const fetchData = async () => {
        try {
            const response = await fetch('/api/introSection');
            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }
            const data = await response.json();
            setIntroData({
                title: data.title,
                sections: data.paragraphs.sections,
                images: data.images.images
            });
        } catch (error) {
            console.error('Error fetching data:', error);
            setError(error.message);
        }
    };

    useEffect(() => {
        fetchData();
    }, []);

    const handleClick = (index) => {
        setSelectedBlock(index === selectedBlock ? null : index);
    };

    if (error) {
        return <div>Error loading Intro Section: {error}</div>;
    }

    return (
        <section className="intro-section">
            <h2>{introData.title || 'О нашей компании'}</h2>
            <div className="advantage-container">
                {introData.images.map((img, index) => (
                    <div key={index} className="advantage-card" onClick={() => handleClick(index)}>
                        <img className='advantage-icon' src={img} alt={`Изображение ${index + 1}`} />
                        {selectedBlock === index && (
                            <div className="text-overlay">
                                <h3>{introData.sections[index].header}</h3>
                                <p>{introData.sections[index].content}</p>
                            </div>
                        )}
                    </div>
                ))}
            </div>
        </section>
    );
}

export default IntroSection;
