import React from 'react';
import { useCart } from './CartContext/CartContext'; // Импортируем контекст корзины
import CartItem from "./CartContext/CartItem/CartItem";
import "./Cart.css";

function Cart() {
  const { cartItems, updateQuantity, removeItem } = useCart(); // Используем контекст

  const totalCost = cartItems.reduce((total, item) => total + item.price * item.quantity, 0);

  return (
    <div className="cart">
      <h2>Ваша корзина</h2>
      {cartItems.length > 0 ? (
        cartItems.map(item => (
          <CartItem 
            key={item.id}
            item={item}
            updateQuantity={updateQuantity}
            removeItem={removeItem}
          />
        ))
      ) : (
        <p>Ваша корзина пуста.</p>
      )}
      <div className="total-cost">
        <strong>Общая стоимость: </strong> 
        {totalCost.toLocaleString('ru-RU', { style: 'currency', currency: 'RUB' })}
      </div>
    </div>
  );
}

export default Cart;
