import React, { useState, useEffect } from 'react';
import './IntroSection.css';

function IntroSection() {
    const [introData, setIntroData] = useState({
        title: '',
        sections: [],
        images: [] // Инициализируем как пустой массив
    });
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch('http://localhost:3000/api/introSection');
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                const data = await response.json();

                // Обновляем состояние с проверкой, что sections и images являются массивами
                setIntroData({
                    title: data.title,
                    sections: Array.isArray(data.sections) ? data.sections : [],
                    images: Array.isArray(data.images) ? data.images : [],
                });
            } catch (error) {
                console.error('Error fetching data:', error);
                setError(error.message);
            }
        };

        fetchData();
    }, []);

    if (error) {
        return <div>Error loading Intro Section: {error}</div>;
    }

    return (
        <section className="intro-section">
            <h2>{introData.title}</h2>
            {introData.sections.map((section, index) => (
                <div key={index}>
                    <h3>{section.header}</h3>
                    <p>{section.content}</p>
                </div>
            ))}
            <div className="gallery">
                {/* Добавляем проверку перед использованием .map() */}
                {Array.isArray(introData.images) && introData.images.map((img, index) => (
                    <div className="image-container" key={index}>
                        <img src={img} alt={`Gallery item ${index + 1}`} loading="lazy" />
                    </div>
                ))}
            </div>
        </section>
    );
}

export default IntroSection;
