import React, { useState } from 'react';
import { useAuth } from '../contexts/AuthContext'; // Предполагаем, что путь к AuthContext таков

const AuthForm = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const { login, isLoading, error } = useAuth(); // Извлекаем функцию login, isLoading и error из контекста

  const handleLogin = async (event) => {
    event.preventDefault();
    if (isLoading) return; // Предотвращаем повторный вход в систему, если процесс уже запущен

    login(username, password); // Вызываем функцию login из контекста с введенными username и password
  };

  return (
    <div className="login-form-container">
      <form onSubmit={handleLogin} className="login-form">
        <h2>Вход в систему</h2>
        <div className="form-group">
          <label htmlFor="username">Имя пользователя</label>
          <input
            type="text"
            id="username"
            name="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
            disabled={isLoading} // Поле неактивно, если идет загрузка
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Пароль</label>
          <input
            type="password"
            id="password"
            name="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            disabled={isLoading} // Поле неактивно, если идет загрузка
          />
        </div>
        {error && <div className="alert alert-danger" role="alert">{error}</div>} {/* Отображение ошибки */}
        <button type="submit" className="btn btn-primary" disabled={isLoading}>
          {isLoading ? 'Загрузка...' : 'Войти'} {/* Текст кнопки изменяется в зависимости от состояния загрузки */}
        </button>
      </form>
    </div>
  );
};

export default AuthForm;
