// UserProfile.js
import React from 'react';
import { useAuth } from '../AuthContext/AuthContext';
import AuthForm from '../AuthForm/AuthForm'; 
import '../AuthForm/AuthForm.css'

const UserProfile = () => {
  const { user, logout } = useAuth();

  // Если пользователь не авторизован, показываем универсальную форму AuthForm
  if (!user) {
    return (
      <div>
        <h1>Личный кабинет</h1>
        <AuthForm />
      </div>
    );
  }

  // Контент для администратора
  const adminContent = (
    <div>
      <h2>Панель администратора</h2>
      {/* Элементы панели администратора */}
      <button onClick={logout}>Выйти</button>
    </div>
  );

  // Контент для обычного пользователя
  const userContent = (
    <div>
      <h2>Привет, {user.name}</h2>
      <h3>Ваши заказы:</h3>
      {/* Список заказов пользователя */}
      <button onClick={logout}>Выйти</button>
    </div>
  );

  // Отображение интерфейса в зависимости от роли пользователя
  return (
    <div>
      <h1>Личный кабинет</h1>
      {user.role === 'admin' ? adminContent : userContent}
    </div>
  );
};

export default UserProfile;
