import React, { useState, useContext, useEffect } from 'react';

import { AuthContext } from '../AuthContext/AuthContext';
import AuthForm from '../AuthForm/AuthForm'; // Предполагаем, что AuthForm находится в той же папке
import { useCart } from '../CartContext/CartContext';
import './PlaceOrder.css';

const PlaceOrderComponent = () => {
  const { placeOrder, orderStatus } = useCart();
  const { user } = useContext(AuthContext);
  const [showModal, setShowModal] = useState(false);
  const [showAuthForm, setShowAuthForm] = useState(false);

  useEffect(() => {
    if (orderStatus === 'success') {
      setShowModal(true);
      setTimeout(() => setShowModal(false), 3000);
    }
  }, [orderStatus]);

  const handlePlaceOrder = () => {
    if (!user) {
      setShowAuthForm(true);
    } else {
      placeOrder();
    }
  };

  return (
    <div>
      <button className="place-order-button" onClick={handlePlaceOrder}>Оформить заказ</button>

      {showAuthForm && <AuthForm />}

      <div className={`modal ${showModal ? 'active' : ''}`}>
        <div className="modal-header">Заказ Оформлен!</div>
        <p>Ваш заказ успешно оформлен и скоро будет обработан.</p>
      </div>

      <p className={`order-success-message ${orderStatus === 'success' ? 'active' : ''}`}>
        Ваш заказ успешно оформлен!
      </p>
    </div>
  );
};

export default PlaceOrderComponent;
