import React from 'react';
import { useFurniture } from './context/FurnitureContext';

const SizeSelector = () => {
    const { size, setSize } = useFurniture();

    const handleChange = (e) => {
        const { name, value } = e.target;
        setSize(prevSize => ({ ...prevSize, [name]: Number(value) }));
    };

    return (
        <div className="selector size-selector">
            <h3>Выберите размеры</h3>
            <label>
                Ширина:
                <input type="number" name="width" value={size.width} onChange={handleChange} className="size-input" />
            </label>
            <label>
                Высота:
                <input type="number" name="height" value={size.height} onChange={handleChange} className="size-input" />
            </label>
            <label>
                Глубина:
                <input type="number" name="depth" value={size.depth} onChange={handleChange} className="size-input" />
            </label>
        </div>
    );
};

export default SizeSelector;
