import React, { useState, useEffect } from 'react';
import './IntroSection.css';

function IntroSection() {
    const [introData, setIntroData] = useState({ title: '', paragraphs: [], images: [] });
    const [error, setError] = useState(null);

    // Асинхронная функция для загрузки данных
    const fetchData = async () => {
        try {
            const response = await fetch('/api/introSection');
            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }
            const data = await response.json();
            setIntroData(data);
        } catch (error) {
            console.error('Error fetching data:', error);
            setError(error.message);
        }
    };

    // Запуск функции fetchData при монтировании компонента
    useEffect(() => {
        fetchData();
    }, []);

    // Отображение ошибки, если она есть
    if (error) {
        return <div>Error loading Intro Section: {error}</div>;
    }

    // Отображение контента раздела
    return (
        <section className="intro-section">
            <h2>{introData.title || 'О нашей компании'}</h2>
            {introData.paragraphs.map((paragraph, index) => (
                <p key={index}>{paragraph}</p>
            ))}
            <div className="gallery">
                {introData.images.map((img, index) => (
                    <img key={index} src={img} alt={`Изображение ${index + 1}`} />
                ))}
            </div>
        </section>
    );
}

export default IntroSection;
