import React, { useState } from 'react';
import { useAuth } from '../AuthContext/AuthContext';
import 'bootstrap/dist/css/bootstrap.min.css';
import './AuthForm.css';

const AuthForm = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState(''); // Новое состояние для email
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState(''); // Новое состояние для подтверждения пароля
  const [localError, setLocalError] = useState('');
  const { user, isLoading, error, login, register, logout } = useAuth();

  const isValidUsername = (username) => /^[a-zA-Z0-9_]+$/.test(username);
  const isValidEmail = (email) => /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email); // Функция валидации email
  const isValidPassword = (password) => /^(?=.*[A-Za-z])(?=.*\d)[A-Za-z\d]{6,}$/.test(password);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLocalError('');

    if (!isValidUsername(username)) {
      setLocalError('Имя пользователя может содержать только латинские буквы, цифры и подчеркивания.');
      return;
    }
    if (!isValidEmail(email)) {
      setLocalError('Введите корректный email.');
      return;
    }
    if (!isValidPassword(password)) {
      setLocalError('Пароль должен быть не менее 6 символов и содержать хотя бы одну цифру.');
      return;
    }
    if (!isLogin && password !== confirmPassword) {
      setLocalError('Пароли не совпадают.');
      return;
    }
    try {
      if (isLogin) {
        await login(username, password);
      } else {
        await register(username, email, password);
      }
    } catch (error) {
      setLocalError(error.message || 'Ошибка аутентификации');
    }
  };

  const handleLogout = () => {
    logout();
    setUsername('');
    setEmail(''); // Сбрасываем email
    setPassword('');
    setConfirmPassword(''); // Сбрасываем подтверждение пароля
  };

  const displayError = localError || error;

  return (
    <div className="auth-form-wrapper">
      <div className="auth-form-container">
        <h2 className="auth-form-title">{isLogin ? 'Вход в систему' : 'Регистрация'}</h2>
        {displayError && <div className="alert alert-danger">{displayError}</div>}
        {user ? (
          <div>
            <div className="alert alert-success">Вы вошли как {user.name}</div>
            <button onClick={handleLogout} className="btn btn-danger">
              Выйти
            </button>
          </div>
        ) : (
          <form onSubmit={handleSubmit} className="auth-form">
            <div className="mb-3">
              <label htmlFor="username" className="form-label">Имя пользователя:</label>
              <input
                type="text"
                className="form-control"
                id="username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
              />
            </div>
            {!isLogin && (
              <div className="mb-3">
                <label htmlFor="email" className="form-label">Email:</label>
                <input
                  type="email"
                  className="form-control"
                  id="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                />
              </div>
            )}
            <div className="mb-3">
              <label htmlFor="password" className="form-label">Пароль:</label>
              <input
                type="password"
                className="form-control"
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
            {!isLogin && (
              <div className="mb-3">
                <label htmlFor="confirmPassword" className="form-label">Подтвердите пароль:</label>
                <input
                  type="password"
                  className="form-control"
                  id="confirmPassword"
                  value={confirmPassword}
                  onChange={(e) => setConfirmPassword(e.target.value)}
                  required
                />
              </div>
            )}
            <button type="submit" className="btn btn-primary" disabled={isLoading}>
              {isLoading ? 'Загрузка...' : isLogin ? 'Войти' : 'Зарегистрироваться'}
            </button>
            <button onClick={() => setIsLogin(!isLogin)} className="toggle-button btn btn-link">
              {isLogin ? 'Нет аккаунта? Зарегистрируйтесь' : 'Уже есть аккаунт? Войдите'}
            </button>
          </form>
        )}
      </div>
    </div>
  );
};

export default AuthForm;
