import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../AuthContext/AuthContext';
import 'bootstrap/dist/css/bootstrap.min.css';
import './AuthForm.css';

const AuthForm = () => {
  const [formState, setFormState] = useState('login'); // 'login', 'register', 'resetPassword'
  const { isLoading, login, user, error: authError, register, resetPassword } = useAuth();
  const [error, setError] = useState(null);
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    if (user) navigate('/dashboard');
    if (authError) setError(authError);
  }, [user, navigate, authError]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (isLoading) return;

    setError(null);

    try {
      switch (formState) {
        case 'login':
          await login(username, password);
          break;
        case 'register':
          await register(username, email, password);
          break;
        case 'resetPassword':
          await resetPassword(email);
          alert('If an account exists, password reset instructions have been sent to your email.');
          setFormState('login');
          break;
        default:
          throw new Error('Unknown form state');
      }
    } catch (err) {
      setError(err.message || 'An error occurred during authentication.');
    }
  };

  const toggleFormState = (newState) => {
    setError(null);
    setUsername('');
    setEmail('');
    setPassword('');
    setFormState(newState);
  };


  return (
    <div className="auth-form-wrapper">
      <div className="auth-form-container">
        <h2 className="auth-form-title">
          {formState === 'login' && 'Вход в систему'}
          {formState === 'register' && 'Регистрация'}
          {formState === 'resetPassword' && 'Сброс пароля'}
        </h2>
        {error && (
          <div className="alert alert-danger" role="alert">
            {error}
          </div>
        )}
        <form onSubmit={handleSubmit} className="auth-form">
          {formState !== 'resetPassword' && (
            <div className="mb-3">
              <label htmlFor="username" className="form-label">Имя пользователя:</label>
              <input
                type="text"
                className="form-control"
                id="username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required={formState !== 'resetPassword'}
              />
            </div>
          )}
          {(formState === 'register' || formState === 'resetPassword') && (
            <div className="mb-3">
              <label htmlFor="email" className="form-label">Email:</label>
              <input
                type="email"
                className="form-control"
                id="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>
          )}
          {formState !== 'resetPassword' && (
            <div className="mb-3">
              <label htmlFor="password" className="form-label">Пароль:</label>
              <input
                type="password"
                className="form-control"
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
          )}
          <button type="submit" className="btn btn-primary" disabled={isLoading}>
            {isLoading ? 'Загрузка...' : formState === 'login' ? 'Войти' : formState === 'register' ? 'Зарегистрироваться' : 'Сбросить пароль'}
          </button>
        </form>
        {formState !== 'resetPassword' && (
          <button onClick={() => toggleFormState('resetPassword')} className="toggle-button btn btn-link">
            Забыли пароль?
          </button>
        )}
        <button onClick={() => toggleFormState(formState === 'login' ? 'register' : 'login')} className="toggle-button btn btn-link">
          {formState === 'login' ? 'Нет аккаунта? Зарегистрируйтесь' : 'Уже есть аккаунт? Войдите'}
        </button>
      </div>
    </div>
  );
};

export default AuthForm;
