import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button, Alert } from 'react-bootstrap';
import { useCart } from '../CartContext/CartContext';
import { Link, useLocation } from 'react-router-dom'; 
import './Catalog.css';

function Catalog({ isHomePage = false}) { 
    const { addToCart } = useCart();
    const [products, setProducts] = useState([]);
    const [addedToCart, setAddedToCart] = useState(false);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const token = localStorage.getItem('token');
                const response = await fetch('/api/products', {
                    headers: token ? { 'Authorization': `Bearer ${token}` } : {},
                });

                if (!response.ok) {
                    throw new Error(`Ошибка HTTP, статус: ${response.status}`);
                }
                const data = await response.json();

                // Предполагаем, что данные приходят в формате { rows: [...] }
                if (data.rows && Array.isArray(data.rows)) {
                    setProducts(data.rows);
                } else {
                    throw new Error('Полученные данные не являются массивом');
                }
            } catch (error) {
                console.error('Ошибка при получении данных:', error);
                setError(error.message);
            }
        };

        fetchProducts();
    }, []);

    const handleAddToCart = (product) => {
        addToCart(product);
        setAddedToCart(true);
        setTimeout(() => setAddedToCart(false), 2000);
    };
 const displayedProducts = isHomePage ? products(0, 3): products;
    if (error) {
        return <Alert variant="danger">Ошибка: {error}</Alert>;
    }

    return (
        <Container className="catalog-container my-5">
            {addedToCart && <Alert variant="success">Товар добавлен в корзину!</Alert>}
            <Row>
                <Col xs={12} className="text-center mb-4">
                    <h2>Каталог товаров</h2>
                </Col>
            </Row>
            <Row className="justify-content-center">
                {products.map(product => (
                    <Col key={product.id} xs={12} sm={6} md={4} lg={3} className="mb-4 d-flex align-items-stretch">
                        <Card className="product-card">
                            <Card.Img variant="top" src={product.image} alt={product.title} />
                            <Card.Body className="d-flex flex-column">
                                <Card.Title>{product.title}</Card.Title>
                                <Card.Text className="mt-auto">
                                    {`${product.price} Р`}
                                </Card.Text>
                                <Button variant="success" className="mt-auto" onClick={() => handleAddToCart(product)}>
                                    Купить
                                </Button>
                            </Card.Body>
                        </Card>
                    </Col>
                ))}
            </Row>
            {isHomePage &&( 
                <Row className='justify-content-center'>
                    <Col xs={12} className='text-center'>
                      <Button variant='outline-primary' as= {Link} to = "/catalog" >Посмотреть больше</Button>  


                        </Col>

                        </Row>

            )}
        </Container>
    );
}

export default Catalog;
