import React from 'react';
import { useFurniture } from '../context/FurnitureContext'; // Проверьте пути

const MaterialSelector = () => {
    const { setMaterial, materials } = useFurniture();

    return (
        <div className="selector material-selector">
            <h3>Выберите материал</h3>
            <select onChange={e => setMaterial(e.target.value)} className="material-select">
                <option value="">Выберите материал</option>
                {materials.map(mat => (
                    <option key={mat.material_id} value={mat.name}>
                        {mat.name}
                    </option>
                ))}
            </select>
        </div>
    );
};

export default MaterialSelector;
