import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button, Alert, Form } from 'react-bootstrap';
import { useCart } from '../CartContext/CartContext';
import { Link } from 'react-router-dom';
import './Catalog.css';

function Catalog({ isHomePage }) {
    const { addToCart } = useCart();
    const [products, setProducts] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const response = await fetch('/api/products');
                if (!response.ok) {
                    throw new Error(`Ошибка HTTP: статус ${response.status}`);
                }
                const data = await response.json();
                setProducts(data);
            } catch (error) {
                setError(`Ошибка при загрузке данных: ${error.message}`);
            }
        };

        fetchProducts();
    }, []);

    if (error) {
        return <Alert variant="danger">{error}</Alert>;
    }

    // Логика отображения для главной страницы и страницы каталога
    const displayProducts = isHomePage ? products.slice(0, 3) : products;

    return (
        <Container className="catalog-container my-5">
            <Row>
                <Col xs={12} className="text-center mb-4">
                    <h2>Каталог товаров</h2>
                </Col>
            </Row>
            <Row className="justify-content-center">
                {displayProducts.map((product) => (
                    <Col key={product.id} xs={12} sm={6} md={4} lg={3} className="mb-4">
                        <Card className="product-card">
                            <Card.Img variant="top" src={product.image} alt={product.title} />
                            <Card.Body>
                                <Card.Title>{product.title}</Card.Title>
                                <Card.Text>{`${product.price} Р`}</Card.Text>
                                <Button variant="success" onClick={() => addToCart(product)}>Купить</Button>
                            </Card.Body>
                        </Card>
                    </Col>
                ))}
            </Row>
            {isHomePage && (
                <Row className="justify-content-center">
                    <Col xs={12} className="text-center">
                        <Link to="/catalog">
                            <Button variant="outline-primary">Посмотреть все товары</Button>
                        </Link>
                    </Col>
                </Row>
            )}
        </Container>
    );
}

export default Catalog;
