import React, { useState, useEffect } from 'react';
import './IntroSection.css';

function ModalWindow({ show, onClose, content }) {
    if (!show) {
        return null;
    }

    return (
        <div className="modal-backdrop" onClick={onClose}>
            <div className="modal-content" onClick={e => e.stopPropagation()}>
                <h3>{content.header}</h3>
                <p>{content.content}</p>
                <button className="close-button" onClick={onClose}>0</button>
            </div>
        </div>
    );
}

function IntroSection() {
    const [introData, setIntroData] = useState({ title: '', sections: [], images: [] });
    const [selectedBlock, setSelectedBlock] = useState(null);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = async () => {
        try {
            const response = await fetch('/api/introSection');
            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }
            const data = await response.json();
            setIntroData({
                title: data.title,
                sections: data.paragraphs.sections,
                images: data.images.images
            });
        } catch (error) {
            console.error('Error fetching data:', error);
            setError(error.message);
        }
    };

    const handleClick = (index) => {
        setSelectedBlock(index);
    };

    if (error) {
        return <div>Error loading Intro Section: {error}</div>;
    }

    return (
        <section className="intro-section">
            <h2>{introData.title || 'О нашей компании'}</h2>
            <div className="advantage-container">
                {introData.images.map((img, index) => (
                    <div key={index} className="advantage-card" onClick={() => handleClick(index)}>
                        <img src={img} alt={`Изображение ${index + 1}`} />
                        <div className="hover-text">Узнать больше</div>
                    </div>
                ))}
            </div>
            {selectedBlock !== null && (
                <ModalWindow 
                    show={selectedBlock !== null}
                    onClose={() => setSelectedBlock(null)}
                    content={introData.sections[selectedBlock]}
                />
            )}
        </section>
    );
}

export default IntroSection;
