import React, { useState, useEffect, useRef } from 'react';
import { Container, Row, Col, Card, Button, Alert } from 'react-bootstrap';
import { useCart } from '../CartContext/CartContext';

function Catalog() {
    const { addToCart } = useCart();
    const [products, setProducts] = useState([]);
    const [addedToCart, setAddedToCart] = useState(false);
    const addedToCartRef = useRef(addedToCart); // Создаем ссылку для отслеживания addedToCart
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchProducts = async () => {
            setError(null);
            try {
                const response = await fetch('/api/products');
                if (!response.ok) {
                    throw new Error(`HTTP error: status ${response.status}`);
                }
                const data = await response.json();
                setProducts(data);
            } catch (error) {
                console.error('Error fetching products:', error);
                setError(error.message);
            }
        };

        fetchProducts();
    }, []);

    useEffect(() => {
        addedToCartRef.current = addedToCart;
        // Очистка использует текущее значение addedToCart из addedToCartRef
        return () => {
            if (addedToCartRef.current) {
                setAddedToCart(false);
            }
        };
    }, [addedToCart]); // Теперь addedToCart включена в зависимости, как требует ESLint

    const handleAddToCart = (product) => {
        addToCart(product);
        setAddedToCart(true);
        setTimeout(() => setAddedToCart(false), 2000);
    };
    return (
        <Container className="my-5">
          {addedToCart && <Alert variant="success">Product added to cart!</Alert>}
          {error && <Alert variant="danger">Error: {error}</Alert>}
          <Row>
            {products.map(product => (
              <Col key={product.id} xs={12} sm={6} md={4} lg={3} className="mb-4">
                <Card>
                  <Card.Img variant="top" src={product.image} alt={product.title} />
                  <Card.Body>
                    <Card.Title>{product.title}</Card.Title>
                    <Card.Text>{product.description}</Card.Text>
                    <Card.Text>{`${product.price} RUB`}</Card.Text>
                    <Button variant="primary" onClick={() => handleAddToCart(product)}>
                      Add to Cart
                    </Button>
                  </Card.Body>
                </Card>
              </Col>
            ))}
          </Row>
        </Container>
      );
    };

    export default Catalog;
