import React, { useState, useContext, useEffect } from 'react';
import { AuthContext } from '../AuthContext/AuthContext';
import AuthForm from '../AuthForm/AuthForm';
import { useCart } from '../CartContext/CartContext';
import './PlaceOrder.css';

const PlaceOrderComponent = () => {
  const { cartItems, placeOrder, orderStatus, setOrderStatus } = useCart();
  const { isAuthenticated } = useContext(AuthContext);
  const [showAuthModal, setShowAuthModal] = useState(false);
  const [showEmptyCartMessage, setShowEmptyCartMessage] = useState(false);

  useEffect(() => {
    // Этот эффект отвечает за сброс статуса заказа
    let timeout;
    if (orderStatus === 'success') {
      timeout = setTimeout(() => setOrderStatus(null), 3000);
    }
    return () => clearTimeout(timeout); // Очищаем таймер при размонтировании компонента
  }, [orderStatus, setOrderStatus]);

  const handlePlaceOrderClick = () => {
    if (!isAuthenticated) {
      setShowAuthModal(true);
      return;
    }

    if (cartItems.length === 0) {
      setShowEmptyCartMessage(true);
      setTimeout(() => setShowEmptyCartMessage(false), 3000);
      return;
    }

    placeOrder(); // Вызываем функцию оформления заказа из контекста корзины
  };

  return (
    <div>
      <button className="place-order-button" onClick={handlePlaceOrderClick}>
        Оформить заказ
      </button>

      {showAuthModal && (
        <div className="auth-modal active">
          <div className="auth-modal-content">
            <div className="auth-modal-header">
              <h5>Для оформления заказа, пожалуйста, выполните вход в свой аккаунт</h5>
              <button onClick={() => setShowAuthModal(false)} className="close">&times;</button>
            </div>
            <div className="auth-modal-body">
              <AuthForm onClose={() => setShowAuthModal(false)} />
            </div>
          </div>
        </div>
      )}

      {showEmptyCartMessage && (
        <div className="empty-cart-message">
          Ваша корзина пуста. Пожалуйста, добавьте товары, прежде чем оформлять заказ.
        </div>
      )}

      {orderStatus === 'error' && (
        <p className="order-error-message active">
          Произошла ошибка при оформлении заказа. Пожалуйста, попробуйте еще раз.
        </p>
      )}

      {orderStatus === 'success' && (
        <p className="order-success-message active">
          Ваш заказ успешно оформлен!
        </p>
      )}
    </div>
  );
};

export default PlaceOrderComponent;
