import React, { useEffect, useState } from 'react';
import { useCart } from '../CartContext/CartContext';
import CartItem from "./CartItem/CartItem";
import PlaceOrderComponent from '../PlaceOrder/PlaceOrder';
import { useNavigate } from 'react-router-dom';
import "./Cart.css";

function Cart() {
  const { cartItems, removeItem, placeOrder, updateQuantity } = useCart();
  const [isUserAuthenticated, setIsUserAuthenticated] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const checkUserAuthentication = async () => {
      // Функция должна определить, авторизован ли пользователь
      const token = localStorage.getItem('token');
      setIsUserAuthenticated(!!token); // Простая проверка наличия токена
    };

    checkUserAuthentication();
  }, []);

  const totalCost = cartItems.reduce((total, item) => total + item.price * item.quantity, 0);

  // Функция для перенаправления пользователя обратно в каталог
  const handleBackToShopping = () => {
    navigate('/catalog');
  };

  return (
    <div className="cart-container">
      <h2 className="cart-title">Ваша корзина</h2>
      <div className="cart-items-list">
        {cartItems.length > 0 ? (
          <>
            {cartItems.map(item => (
              <CartItem 
                key={item.id}
                item={item}
                updateQuantity={(newQuantity) => updateQuantity(item.id, newQuantity)}
                removeItem={() => removeItem(item.id)}
              />
            ))}
            <div className="cart-checkout">
              <div className="cart-total">
                <strong>Общая стоимость:</strong> {totalCost.toLocaleString('ru-RU', { style: 'currency', currency: 'RUB' })}
              </div>
              {isUserAuthenticated ? (
                <PlaceOrderComponent placeOrder={placeOrder} />
              ) : (
                <button onClick={() => navigate('/login')}>Войдите, чтобы оформить заказ</button>
              )}
            </div>
          </>
        ) : (
          <div className="cart-empty-message">
            <p>Ваша корзина пуста.</p>
            <button 
              className="btn-back-to-shopping" 
              onClick={handleBackToShopping}
            >
              Вернуться к покупкам
            </button>
          </div>
        )}
      </div>
    </div>
  );
}

export default Cart;
