import React, { createContext, useContext, useReducer, useEffect } from 'react';

export const AuthContext = createContext();

const initialState = {
  isAuthenticated: false,
  user: null,
  token: localStorage.getItem('token'),
  isLoading: false,
  error: null,
};

const authReducer = (state, action) => {
  switch (action.type) {
    case 'REQUEST':
      return { ...state, isLoading: true, error: null };
    case 'LOGIN_SUCCESS':
    case 'REGISTER_SUCCESS':
      localStorage.setItem('token', action.payload.token);
      return {
        ...state,
        isAuthenticated: true,
        user: action.payload.user,
        token: action.payload.token,
        isLoading: false,
        error: null,
      };
    case 'LOGOUT':
      localStorage.removeItem('token');
      return { ...state, isAuthenticated: false, user: null, token: null, error: null };
    case 'ERROR':
      // Преобразование сообщения об ошибке с сервера на понятный пользователю язык
      const errorMessage = action.payload === 'User already exists' ? 'Пользователь с таким логином или почтой уже существует' :
                           action.payload === 'Invalid credentials' ? 'Неверный логин или пароль' :
                           action.payload; // Для остальных ошибок используем текст как есть
      return { ...state, isLoading: false, error: errorMessage };
    default:
      return state;
  }
};

export const AuthProvider = ({ children }) => {
  const [state, dispatch] = useReducer(authReducer, initialState);

  useEffect(() => {
    const initializeAuth = async () => {
      const token = localStorage.getItem('token');
      if (token) {
        // Отправляем токен на сервер для проверки
        try {
          const response = await fetch('/api/users/validateToken', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
              'Authorization': `Bearer ${token}` // Предполагается использование схемы Bearer
            },
          });

          if (!response.ok) {
            throw new Error('Token validation failed');
          }

          const { user } = await response.json();
          dispatch({ type: 'LOGIN_SUCCESS', payload: { token, user } });
        } catch (error) {
          localStorage.removeItem('token');
          dispatch({ type: 'LOGOUT' });
        }
      }
    };
    initializeAuth();
  }, []);

  const login = async (username, password) => {
    dispatch({ type: 'REQUEST' });
    try {
      const response = await fetch('/api/users/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, password }),
      });

      if (!response.ok) {
        throw new Error('Ошибка входа');
      }

      const { token, user } = await response.json();
      dispatch({ type: 'LOGIN_SUCCESS', payload: { token, user } });
    } catch (error) {
      dispatch({ type: 'ERROR', payload: error.toString() });
    }
  };

  const register = async (username, password, email) => {
    dispatch({ type: 'REQUEST' });
    try {
      const response = await fetch('/api/users/register', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, password, email }),
      });

      if (!response.ok) {
        throw new Error('Ошибка регистрации');
      }

      const { token, user } = await response.json();
      localStorage.setItem('token', token); // Сохраняем токен
      dispatch({ type: 'LOGIN_SUCCESS', payload: { token, user } }); // Обновляем состояние как аутентифицированное

      // Дополнительно: Можете реализовать переход на страницу личного кабинета или показать уведомление
    } catch (error) {
      dispatch({ type: 'ERROR', payload: error.toString() });
    }
  };
  const logout = () => {
    dispatch({ type: 'LOGOUT' });
  };

  const authContextValue = {
    ...state,
    login,
    register,
    logout,
  };

  return <AuthContext.Provider value={authContextValue}>{children}</AuthContext.Provider>;
};

export const useAuth = () => useContext(AuthContext);
