import React, { createContext, useContext, useState } from 'react';

const FurnitureContext = createContext();

export const useFurniture = () => useContext(FurnitureContext);

export const FurnitureProvider = ({ children }) => {
    const [material, setMaterial] = useState('');
    const [size, setSize] = useState({ width: '', height: '', depth: '' });
    const [customizations, setCustomizations] = useState([]);
    const [image, setImage] = useState('');

    const value = {
        material, setMaterial, size, setSize, customizations, setCustomizations, image, setImage
    };

    return (
        <FurnitureContext.Provider value={value}>
            {children}
        </FurnitureContext.Provider>
    );
};
