import React, { useState, useEffect } from 'react';
import { useAuth } from '../AuthContext/AuthContext';
import AuthForm from '../AuthForm/AuthForm';
import AdminPanel from './AdminPanel';
import '../AuthForm/AuthForm.css';

const UserProfile = () => {
  const { user, logout } = useAuth();
  const [orders, setOrders] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    if (user) {
      fetchOrders();
    }
  }, [user]);

  const fetchOrders = () => {
    fetch('/api/orders/myOrders', {
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('token')}`
      }
    })
    .then(response => {
      if (!response.ok) {
        throw new Error(`Ошибка загрузки заказов: ${response.statusText}`);
      }
      return response.json();
    })
    .then(setOrders)
    .catch(error => {
      console.error('Error fetching orders:', error);
      setError(error.message);
    });
  };

  const cancelOrder = (orderId) => {
    fetch(`/api/orders/cancel/${orderId}`, {
      method: 'POST',
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('token')}`,
        'Content-Type': 'application/json'
      },
    })
    .then(response => {
      if (!response.ok) {
        throw new Error(`Ошибка отмены заказа: ${response.statusText}`);
      }
      fetchOrders(); // Обновить список заказов после отмены
    })
    .catch(error => {
      console.error('Error cancelling order:', error);
      setError(error.message);
    });
  };

  const OrderList = () => (
    <div>
      {orders.length > 0 ? orders.map(order => (
        <div key={order.id} className="order">
          <h4>Заказ №{order.id}</h4>
          <p>Статус: {order.status}</p>
          {/* Детали заказа */}
          {order.status !== 'Отменен' && (
            <button onClick={() => cancelOrder(order.id)} className="btn btn-warning">
              Отменить заказ
            </button>
          )}
        </div>
      )) : (
        <p>У вас пока нет заказов.</p>
      )}
      {error && <p className="error">{error}</p>}
    </div>
  );

  const renderUserContent = () => (
    <div className="user-content">
      <h2>Привет, {user.name}</h2>
      <h3>Ваши заказы:</h3>
      <OrderList />
      <button onClick={logout} className="btn btn-danger">Выйти</button>
    </div>
  );

  return (
    <div className="user-profile">
      <h1>Личный кабинет</h1>
      {!user ? (
        <AuthForm />
      ) : user.role === 'admin' ? (
        <AdminPanel/>
      ) : (
        renderUserContent()
      )}
    </div>
  );
};

export default UserProfile;
