import React, { createContext, useState, useContext, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const CartContext = createContext();

export const useCart = () => useContext(CartContext);

const getUserIdFromToken = () => {
  const token = localStorage.getItem('token');
  if (!token) return 'anonymous';
  try {
    const payload = JSON.parse(atob(token.split('.')[1]));
    return payload.userId;
  } catch (error) {
    console.error("Ошибка при обработке токена:", error);
    return 'anonymous';
  }
};

export const CartProvider = ({ children }) => {
  const navigate = useNavigate();
  const [isUserAuthenticated, setIsUserAuthenticated] = useState(!!localStorage.getItem('token'));
  const [userId, setUserId] = useState(getUserIdFromToken());
  const [orderStatus, setOrderStatus] = useState(null);
  const [cartItems, setCartItems] = useState(() => {
    const savedCart = localStorage.getItem(`cart-${userId}`);
    return savedCart ? JSON.parse(savedCart) : [];
  });

  useEffect(() => {
    const handleTokenChange = () => {
      const newUserId = getUserIdFromToken();
      setIsUserAuthenticated(!!localStorage.getItem('token'));
      if (userId !== newUserId) {
        console.log(`Обновление userId: ${userId} -> ${newUserId}`);
        setUserId(newUserId);
        // Загружаем данные корзины для нового пользователя
        const savedCart = localStorage.getItem(`cart-${newUserId}`);
        setCartItems(savedCart ? JSON.parse(savedCart) : []);
      }
    };

    // Подписываемся на событие изменения localStorage
    window.addEventListener('storage', handleTokenChange);

    // Вызываем функцию при монтировании компонента
    handleTokenChange();

    // Отписываемся от события при размонтировании компонента
    return () => window.removeEventListener('storage', handleTokenChange);
  }, [userId]);

  useEffect(() => {
    localStorage.setItem(`cart-${userId}`, JSON.stringify(cartItems));
  }, [userId, cartItems]);

  const addToCart = (product) => {
    setCartItems(prevItems => {
      const itemIndex = prevItems.findIndex(item => item.id === product.id);
      if (itemIndex !== -1) {
        const updatedItems = [...prevItems];
        updatedItems[itemIndex].quantity += 1;
        return updatedItems;
      }
      return [...prevItems, { ...product, quantity: 1 }];
    });
  };

  const updateQuantity = (productId, quantity) => {
    setCartItems(prevItems =>
      prevItems.map(item =>
        item.id === productId ? { ...item, quantity: Math.max(1, quantity) } : item
      )
    );
  };

  const removeItem = (productId) => {
    setCartItems(prevItems => prevItems.filter(item => item.id !== productId));
  };

  const placeOrder = async () => {
    if (!isUserAuthenticated) {
      navigate('/login');
      return;
    }

    if (cartItems.length === 0) {
      setOrderStatus('empty');
      return;
    }

    setOrderStatus('processing');
    const token = localStorage.getItem('token');

    try {
      const response = await fetch('/api/orders/placeOrder', {
        method: 'POST',
        body: JSON.stringify({ items: cartItems }),
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
      });

      if (response.status === 429) {
        setOrderStatus('too-many-requests');
        return;
      }

      if (!response.ok) {
        throw new Error(`Ошибка: ${response.status}`);
      }

      setCartItems([]);
      setOrderStatus('success');
    } catch (error) {
      console.error('Ошибка при оформлении заказа:', error);
      setOrderStatus('error');
    }
  };

  return (
    <CartContext.Provider value={{
      cartItems,
      addToCart,
      updateQuantity,
      removeItem,
      placeOrder,
      orderStatus,
      isUserAuthenticated,
    }}>
      {children}
    </CartContext.Provider>
  );
};

export default CartContext;
