import React, { useState, useEffect } from 'react';
import './IntroSection.css';

function IntroSection() {
    const [introData, setIntroData] = useState({
        title: '',
        sections: [],
        images: [] // Инициализируем как пустой массив
    });
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch('http://localhost:3000/api/introSection');
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                const data = await response.json();

                // Исправляем здесь, обращаясь к data.paragraphs.sections и data.images.images
                setIntroData({
                    title: data.paragraphs.title, // Обновлено для соответствия структуре ответа
                    sections: data.paragraphs.sections ? data.paragraphs.sections : [], // Обновлено для соответствия структуре ответа
                    images: data.images.images ? data.images.images : [], // Обновлено для соответствия структуре ответа
                });
            } catch (error) {
                console.error('Error fetching data:', error);
                setError(error.message);
            }
        };

        fetchData();
    }, []);

    if (error) {
        return <div>Error loading Intro Section: {error}</div>;
    }

    return (
        <section className="intro-section">
            <h2>{introData.title}</h2>
            {introData.sections.map((section, index) => (
                <div key={index}>
                    <h3>{section.header}</h3>
                    <p>{section.content}</p>
                </div>
            ))}
           <div className="gallery">
    {introData.images.map((img, index) => (
        <div className="image-container" key={index}>
            <img src={img} alt={`Gallery item ${index + 1}`} loading="lazy" />
            {/* Пример добавления текста на изображение */}
            <div className="image-text">Описание изображения {index + 1}</div>
        </div>
    ))}
</div>
        </section>
    );
}

export default IntroSection;
