import React, { useState } from 'react';
import { useCart } from '../CartContext/CartContext';
import CartItem from "./CartItem/CartItem";
import AuthForm from '../AuthForm/AuthForm'; // Импортируйте компонент формы авторизации
import { useNavigate } from 'react-router-dom';
import "./Cart.css";

function Cart() {
  const { cartItems, removeItem, placeOrder, updateQuantity, isUserAuthenticated } = useCart();
  const navigate = useNavigate();
  const [showAuthModal, setShowAuthModal] = useState(false); // Добавлено состояние для управления отображением модального окна авторизации

  const totalCost = cartItems.reduce((total, item) => total + item.price * item.quantity, 0);

  const handleLoginClick = () => {
    setShowAuthModal(true); // При клике показываем модальное окно
  };

  const handleCloseModal = () => {
    setShowAuthModal(false); // Функция для закрытия модального окна
  };

  return (
    <div className="cart-container">
      <h2 className="cart-title">Ваша корзина</h2>
      <div className="cart-items-list">
        {cartItems.length > 0 ? (
          <>
            {cartItems.map(item => (
              <CartItem 
                key={item.id}
                item={item}
                updateQuantity={updateQuantity}
                removeItem={() => removeItem(item.id)}
              />
            ))}
            <div className="cart-checkout">
              <div className="cart-total">
                <strong>Общая стоимость:</strong> {totalCost.toLocaleString('ru-RU', { style: 'currency', currency: 'RUB' })}
              </div>
              {isUserAuthenticated ? (
                <button onClick={placeOrder}>Оформить заказ</button>
              ) : (
                <button onClick={handleLoginClick}>Войдите, чтобы оформить заказ</button>
              )}
            </div>
          </>
        ) : (
          <div className="cart-empty-message">
            <p>Ваша корзина пуста.</p>
            <button 
              className="btn-back-to-shopping" 
              onClick={() => navigate('/catalog')}
            >
              Вернуться к покупкам
            </button>
          </div>
        )}
      </div>
      {showAuthModal && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={handleCloseModal}>&times;</span>
            <AuthForm />
          </div>
        </div>
      )}
    </div>
  );
}

export default Cart;
