import React from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import './SliderComponent.css';
import Plant1 from '../../Image/1.png';
import Plant2 from '../../Image/kitchen.png';

const SliderComponent = () => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 600,
    slidesToShow: 3, // Динамическое количество слайдов
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000,
    cssEase: "linear",
    responsive: [
      {
        breakpoint: 768,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1
        }
      }
    ]
  };

  return (
    <div className="slider-container">
      <Slider {...settings}>
        <div className="slide">
          <img src={Plant1} alt="Plant 1" />
          <h2>Plant Collection</h2> {/* Подпись */}
          <button>Подробнее</button> {/* Кнопка */}
        </div>
        <div className="slide">
          <img src={Plant2} alt="Plant 2" />
          <h2>Kitchen Essentials</h2> {/* Подпись */}
          <button>Подробнее</button> {/* Кнопка */}
        </div>
        {/* Можно добавить больше слайдов */}
      </Slider>
    </div>
  );
};

export default SliderComponent;

