import React from 'react';
import { useFurniture } from './context/FurnitureContext';

const CustomizationSelector = () => {
    const { customizations, setCustomizations } = useFurniture();

    const handleChange = (e) => {
        const { name, checked } = e.target;
        setCustomizations({ ...customizations, [name]: checked });
    };

    return (
        <div className="selector customization-selector">
            <h3>Выберите дополнительные опции</h3>
            <label>
                <input type="checkbox" name="extraPadding" checked={customizations.extraPadding || false} onChange={handleChange} />
                Дополнительная подушка
            </label>
            <label>
                <input type="checkbox" name="waterproof" checked={customizations.waterproof || false} onChange={handleChange} />
                Водонепроницаемость
            </label>
        </div>
    );
};

export default CustomizationSelector;
