import React, { useState } from 'react';
import '../Style/FurnitureConstructor.css'; // Поправьте этот путь при необходимости
import kitchenImg from '../../../Image/kitchen.png'; // Предполагаем, что это ваше стандартное изображение
import dresserImg from '../../../Image/dresser.png'; // Предполагаем, что это изображение комода
import wardrobeImg from '../../../Image/wardrobe.png'; // Предполагаем, что это изображение шкафа

const FurnitureConstructor = () => {
  const [type, setType] = useState('ТВ-тумба');
  const [dimensions, setDimensions] = useState({ width: 1075, height: 1000, depth: 300 });
  const [color, setColor] = useState('Белый');
  const [material, setMaterial] = useState('ЛДСП');
  const [shelves, setShelves] = useState(3);
  const [drawers, setDrawers] = useState(3);
  const [price] = useState(17019);

  const [image, setImage] = useState(kitchenImg); // Используйте непосредственно переменную img1

  const handleDimensionChange = (dimension, value) => {
    setDimensions({ ...dimensions, [dimension]: value });
  };

  const handleTypeChange = (newType) => {
    setType(newType);
    switch (newType) {
      case 'ТВ-тумба':
        setImage(kitchenImg);
        break;
      case 'Комод':
        setImage(dresserImg);
        break;
      case 'Шкаф-купе':
        setImage(wardrobeImg);
        break;
      default:
        setImage(kitchenImg);
    }
  };

  return (
    <div className="furniture-constructor">
      <h2 className="title">Конструктор мебели</h2>
      <div className="settings">
        <div className="image-display">
          <img src={image} alt="Мебель" />
        </div>
      </div>
    </div>
  );
};

export default FurnitureConstructor;
