import React from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import MapIcon from '../../Image/Group1.svg'; 
import PhoneImage from '../../Image/Group2.svg';
import MapImage from '../../Image/image 3.png';
import './AboutPage.css'; 

const AboutPage = () => {
  return (
    <Container className="about-section my-5">
      <Row className="align-items-center">
        <Col sm={12} md={6} className="text-center text-md-left">
          <div className="contact-info">
          <p><img src={MapIcon} className="fas fa-map-marker-alt" alt="Иконка"/> г.Белгород, ул. Костюкова 46</p>
          <p><img src = {PhoneImage} className="fas fa-phone" alt = 'Иконка'/> +7(777)777-77-77</p>
          </div>
        </Col>
        <Col sm={12} md={6}>
          <img src={MapImage} alt="Карта" className="img-fluid" />
        </Col>
      </Row>
    </Container>
  );
};

export default AboutPage;
