import React, { useState, useEffect } from 'react';

const SiteStatistics = () => {
  const [stats, setStats] = useState({
    numberOfUsers: 0,
    numberOfVisits: 0,
    numberOfSales: 0
  });
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Здесь должен быть запрос к API для получения статистики сайта
    fetch('/api/statistics')
      .then(response => response.json())
      .then(data => {
        setStats(data);
        setIsLoading(false);
      })
      .catch(error => {
        console.error('Error fetching statistics:', error);
        setIsLoading(false);
      });
  }, []);

  if (isLoading) {
    return <div>Loading statistics...</div>;
  }

  return (
    <div className="site-statistics">
      <h3>Статистика сайта</h3>
      <div>Количество пользователей: {stats.numberOfUsers}</div>
      <div>Количество посещений: {stats.numberOfVisits}</div>
      <div>Количество продаж: {stats.numberOfSales}</div>
    </div>
  );
};

export default SiteStatistics;
