import React, { useState } from 'react';
import { useAuth } from '../AuthContext/AuthContext';

const AuthForm = () => {
  const [formType, setFormType] = useState('login'); // Добавлен стейт для определения типа формы
  const [credentials, setCredentials] = useState({ username: '', password: '', email: '' });
  const { login, register, isLoading, error } = useAuth(); // Добавлен register в хуки

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCredentials(prevCredentials => ({ ...prevCredentials, [name]: value }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (isLoading) return;

    if (formType === 'login') {
      login(credentials.username, credentials.password);
    } else if (formType === 'register') {
      register(credentials.username, credentials.password, credentials.email);
    }
    // Место для логики сброса пароля
  };

  return (
    <div className="auth-form-wrapper">
      <div className="auth-form-container">
        <h2 className="auth-form-title">{formType === 'login' ? 'Вход в систему' : 'Регистрация'}</h2>
        <form onSubmit={handleSubmit}>
          {formType === 'register' && (
            <div className="form-group">
              <label htmlFor="email">Email</label>
              <input
                type="email"
                id="email"
                name="email"
                value={credentials.email}
                onChange={handleChange}
                required={formType === 'register'}
                disabled={isLoading}
              />
            </div>
          )}
          <div className="form-group">
            <label htmlFor="username">Имя пользователя</label>
            <input
              type="text"
              id="username"
              name="username"
              value={credentials.username}
              onChange={handleChange}
              required
              disabled={isLoading}
            />
          </div>
          <div className="form-group">
            <label htmlFor="password">Пароль</label>
            <input
              type="password"
              id="password"
              name="password"
              value={credentials.password}
              onChange={handleChange}
              required
              disabled={isLoading}
            />
          </div>
          {error && <div className="alert alert-danger" role="alert">{error}</div>}
          <button type="submit" className="gradient-button">
            {isLoading ? 'Загрузка...' : (formType === 'login' ? 'Войти' : 'Зарегистрироваться')}
          </button>
          {formType !== 'reset' && (
            <button type="button" className="toggle-button" onClick={() => setFormType(formType === 'login' ? 'register' : 'login')}>
              {formType === 'login' ? 'Зарегистрироваться' : 'Войти'}
            </button>
          )}
          {/* Пока просто переключение, можно добавить логику для сброса пароля */}
          <button type="button" className="toggle-button" onClick={() => setFormType('reset')}>
            Забыли пароль?
          </button>
        </form>
      </div>
    </div>
  );
};

export default AuthForm;
