import React, { useState, useEffect } from 'react';
import './IntroSection.css';



function IntroSection() {
    const [introData, setIntroData] = useState({ title: '', sections: [], images: [] });
    const [selectedSection, setSelectedSection] = useState(null);
    const [ setError] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const token = localStorage.getItem('token');
                const response = await fetch('/api/introSection', {
                    headers: token ? { 'Authorization': `Bearer ${token}` } : {},
                });

                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                const data = await response.json();
                setIntroData({
                    title: data.title,
                    sections: data.paragraphs.sections,
                    images: data.images.images
                });
            } catch (error) {
                console.error('Error fetching data:', error);
                setError(error.message);
            }
        };

        fetchData();
    }, []);

        return (
            <section className="intro-section">
                <h2>{introData.title || 'Почему ваш стоит выбрать именно нас'}</h2>
                <div className="advantages-container">
                    {introData.images.map((img, index) => (
                        <div key={index} className="advantage-card">
                            <div className= 'advantage-icon'>
                            <img src={img} alt={`Изображение ${index + 1}`} />
                            </div>
                            <h3>{selectedSection.header}</h3>
                            <p>{selectedSection.content}</p>
                        </div>
                    ))}
                </div>
            </section>
    );
}

export default IntroSection;
