import React, { useState, useEffect } from 'react';
import { Table, Button } from 'react-bootstrap';

const AdminPanel = () => {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    fetch('/api/users')
      .then(response => response.json())
      .then(data => setUsers(data))
      .catch(error => console.error('Error fetching users:', error));
  }, []);

  const deleteUser = userId => {
    fetch(`/api/users/${userId}`, { method: 'DELETE' })
      .then(() => setUsers(users.filter(user => user.id !== userId)))
      .catch(error => console.error('Error deleting user:', error));
  };

  // Функция для редактирования пользователя (плейсхолдер)
  const onEdit = user => {
    console.log('Edit user:', user);
    // Здесь логика для редактирования пользователя
  };

  return (
    <div className="admin-panel">
      <h2>Панель администратора</h2>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Имя пользователя</th>
            <th>Роль</th>
            <th>Действия</th>
          </tr>
        </thead>
        <tbody>
          {users.map(user => (
            <tr key={user.id}>
              <td>{user.id}</td>
              <td>{user.username}</td>
              <td>{user.role}</td>
              <td>
                <Button variant="primary" onClick={() => onEdit(user)}>Изменить</Button>{' '}
                <Button variant="danger" onClick={() => deleteUser(user.id)}>Удалить</Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
};

export default AdminPanel;
