import React, { useState } from 'react';
import './FurnitureConstructor.css'; // Убедитесь, что этот файл существует и находится в правильной директории

const FurnitureConstructor = () => {
  const [type, setType] = useState('ТВ-тумба');
  const [dimensions, setDimensions] = useState({ width: 1075, height: 1000, depth: 300 });
  const [color, setColor] = useState('Белый');
  const [material, setMaterial] = useState('ЛДСП');
  const [shelves, setShelves] = useState(3);
  const [drawers, setDrawers] = useState(3);
  const [price] = useState(17019); // Здесь должна быть логика для расчета цены

  const [image, setImage] = useState('default-image.png'); // Замените 'default-image.png' на ваше стандартное изображение мебели

  const handleDimensionChange = (dimension, value) => {
    setDimensions({ ...dimensions, [dimension]: value });
    // Здесь можно добавить изменение изображения в зависимости от размеров, если это необходимо
  };

  // Обновление изображения в зависимости от типа мебели
  const handleTypeChange = (newType) => {
    setType(newType);
    switch (newType) {
      case 'ТВ-тумба':
        setImage('tv-stand.png'); // Замените на ваше изображение ТВ-тумбы
        break;
      case 'Комод':
        setImage('dresser.png'); // Замените на ваше изображение комода
        break;
      case 'Шкаф-купе':
        setImage('wardrobe.png'); // Замените на ваше изображение шкафа-купе
        break;
      default:
        setImage('default-image.png'); // Ваше стандартное изображение
    }
  };

  return (
    <div className="furniture-constructor">
      <h2 className="title">Конструктор мебели</h2>
      <div className="settings">
        {/* Selector for furniture type */}
        <div className="setting">
          <label>Выберите тип мебели:</label>
          <select value={type} onChange={(e) => handleTypeChange(e.target.value)}>
            <option value="ТВ-тумба">ТВ-тумба</option>
            <option value="Комод">Комод</option>
            <option value="Шкаф-купе">Шкаф-купе</option>
          </select>
        </div>

        {/* Sliders for dimensions */}
        {/* Схожая структура для 'Ширина', 'Высота' и 'Глубина' */}

        {/* Selectors for color and material */}
        {/* Схожая структура для 'Цвет' и 'Материал' */}

        {/* Number selectors for shelves and drawers */}
        {/* Схожая структура для 'Количество полок' и 'Количество ящиков' */}

        {/* Price and Add to cart button */}
        <div className="price-cart">
          <p>Цена: {price} Р</p>
          <button className="cart-button" onClick={() => alert('Добавлено в корзину')}>Добавить в корзину</button>
        </div>
      </div>

      {/* Image display */}
      <div className="image-display">
        <img src={require(`./images/${image}`)} alt="Furniture" />
      </div>
    </div>
  );
};

export default FurnitureConstructor;
