import React, { useState } from 'react';
import MaterialSelector from '../MaterialSelector';
import SizeSelector from '../SizeSelector';
import CustomizationSelector from './CustomizationSelector';
import './FurnitureConstructor.css';

const FurnitureConstructor = () => {
    const [material, setMaterial] = useState('');
    const [size, setSize] = useState({ width: 0, height: 0, depth: 0 });
    const [customizations, setCustomizations] = useState({});

    const handleSubmit = (event) => {
        event.preventDefault();
        // Здесь можно отправить состояние конструктора (material, size, customizations) на сервер или обработать иначе
        console.log({ material, size, customizations });
    };

    return (
        <form onSubmit={handleSubmit}>
            <h2>Build Your Furniture</h2>
            <MaterialSelector onMaterialChange={setMaterial} />
            <SizeSelector onSizeChange={setSize} />
            <CustomizationSelector onCustomizationsChange={setCustomizations} />
            <button type="submit">Submit Your Design</button>
        </form>
    );
};

export default FurnitureConstructor;
