import React, { useState } from 'react';
import { useAuth } from '../AuthContext/AuthContext'; // Используйте правильный путь импорта
import 'bootstrap/dist/css/bootstrap.min.css';
import './AuthForm.css';

const AuthForm = () => {
  // Состояния для управления формой
  const [isLogin, setIsLogin] = useState(true);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [localError, setLocalError] = useState('');

  // Использование контекста аутентификации
  const { currentUser, login, logout, register } = useAuth(); // Предполагаем, что контекст предоставляет эти функции

  // Валидация входных данных
  const isValidUsername = (username) => /^[a-zA-Z0-9_]+$/.test(username);
  const isValidPassword = (password) => /^(?=.*[A-Za-z])(?=.*\d)[A-Za-z\d]{6,}$/.test(password);

  // Обработчик отправки формы
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLocalError('');

    if (!isValidUsername(username) || !isValidPassword(password)) {
      setLocalError('Имя пользователя должно содержать только латинские буквы, цифры и подчеркивания. Пароль должен быть не менее 6 символов и содержать хотя бы одну цифру.');
      return;
    }

    try {
      if (isLogin) {
        await login(username, password);
      } else {
        await register(username, password);
      }
    } catch (error) {
      // Обработка ошибок аутентификации
      setLocalError(error.message || 'Ошибка аутентификации');
    }
  };

  // Обработчик выхода из системы
  const handleLogout = () => {
    logout();
    setUsername('');
    setPassword('');
  };

  // Отображение ошибок
  const displayError = localError;

  return (
    <div className="auth-form-wrapper">
      <div className="auth-form-container">
        <h2 className="auth-form-title">{isLogin ? 'Вход в систему' : 'Регистрация'}</h2>
        {displayError && <div className="alert alert-danger">{displayError}</div>}
        {currentUser ? (
          <div>
            <div className="alert alert-success">Вы вошли как {currentUser.username}</div>
            <button onClick={handleLogout} className="btn btn-danger">
              Выйти
            </button>
          </div>
        ) : (
          <form onSubmit={handleSubmit} className="auth-form">
            <div className="mb-3">
              <label htmlFor="username" className="form-label">Имя пользователя:</label>
              <input type="text" className="form-control" id="username" value={username} onChange={(e) => setUsername(e.target.value)} required />
            </div>
            <div className="mb-3">
              <label htmlFor="password" className="form-label">Пароль:</label>
              <input type="password" className="form-control" id="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
            </div>
            <button type="submit" className="gradient-button btn btn-primary">
              {isLogin ? 'Войти' : 'Зарегистрироваться'}
            </button>
          </form>
        )}
        {!currentUser && (
          <button onClick={() => setIsLogin(!isLogin)} className="toggle-button btn btn-link">
            {isLogin ? 'Нет аккаунта? Зарегистрируйтесь' : 'Уже есть аккаунт? Войдите'}
          </button>
        )}
      </div>
    </div>
  );
};

export default AuthForm;
