import React, { useState, useContext } from 'react';
import { AuthContext } from '../AuthContext/AuthContext'; 
import 'bootstrap/dist/css/bootstrap.min.css';
import './AuthForm.css';

const AuthForm = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const { login, register } = useContext(AuthContext); 

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setIsLoading(true);

    try {
      if (isLogin) {
        await login(username, password);
      } else {
        await register(username, password);
      }
    } catch (authError) {
      setError(isLogin ? 'Ошибка при входе.' : 'Ошибка при регистрации.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="auth-form-wrapper">
      <div className="auth-form-container">
        <h2 className="auth-form-title">{isLogin ? 'Вход в систему' : 'Регистрация'}</h2>
        {error && <div className="alert alert-danger">{error}</div>}
        <form onSubmit={handleSubmit} className="auth-form">
          <div className="mb-3">
            <label htmlFor="username" className="form-label">Имя пользователя:</label>
            <input
              type="text"
              className="form-control"
              id="username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
          </div>
          <div className="mb-3">
            <label htmlFor="password" className="form-label">Пароль:</label>
            <input type="password"
          className="form-control"
          id="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
      </div>
      <button type="submit" className="gradient-button btn btn-primary" disabled={isLoading}>
        {isLogin ? 'Войти' : 'Зарегистрироваться'}
      </button>
    </form>
    <button onClick={() => setIsLogin(!isLogin)} className="toggle-button btn btn-link">
      {isLogin ? 'Нет аккаунта? Зарегистрируйтесь' : 'Уже есть аккаунт? Войдите'}
    </button>
  </div>
</div>
);
};

export default AuthForm;
