import React, { useState, useEffect } from 'react';
import { useCart } from '../CartContext/CartContext';
import './PlaceOrder.css'; // Убедитесь, что путь к CSS-файлу верный


const PlaceOrderComponent = () => {
  const { placeOrder, orderStatus } = useCart();
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    if (orderStatus === 'success') {
      setShowModal(true);
      setTimeout(() => setShowModal(false), 3000); // Автоматически скрыть модальное окно через 3 секунды
    }
  }, [orderStatus]);

  return (
    <div>
      <button className="place-order-button" onClick={placeOrder}>Оформить заказ</button>

      <div className={`modal ${showModal ? 'active' : ''}`}>
        <div className="modal-header">Заказ Оформлен!</div>
        <p>Ваш заказ успешно оформлен и скоро будет обработан.</p>
      </div>

      {/* Сообщение об успешном оформлении заказа */}
      <p className={`order-success-message ${orderStatus === 'success' ? 'active' : ''}`}>
        Ваш заказ успешно оформлен!
      </p>
    </div>
  );
};

export default PlaceOrderComponent;
