import React, { useState, useContext } from 'react';
import { AuthContext } from '../AuthContext/AuthContext'; 
import 'bootstrap/dist/css/bootstrap.min.css';
import './AuthForm.css';

const AuthForm = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [localError, setLocalError] = useState(''); // Добавлено для ошибок валидации
  const { user, isLoading, error, login, register, logout } = useContext(AuthContext);

  if (user) {
    return <Redirect to="/dashboard" />;
  }
  const isValidUsername = (username) => /^[a-zA-Z0-9_]+$/.test(username);
  const isValidPassword = (password) => /^(?=.*[A-Za-z])(?=.*\d)[A-Za-z\d]{6,}$/.test(password);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLocalError(''); // Очищаем локальные ошибки при новой попытке отправки формы

    if (!isValidUsername(username)) {
      setLocalError('Имя пользователя может содержать только латинские буквы, цифры и подчеркивания.');
      return;
    }
    if (!isValidPassword(password)) {
      setLocalError('Пароль должен быть не менее 6 символов и содержать хотя бы одну цифру.');
      return;
    }
    try {
      if (isLogin) {
        await login(username, password);
      } else {
        await register(username, password);
      }
    } catch (error) {
      // Ошибки с сервера уже обрабатываются в AuthContext
    }
  };

  const handleLogout = () => {
    logout();
    setUsername('');
    setPassword('');
  };

  const displayError = localError || error; // Отображаем ошибку из локального состояния или из контекста

  return (
    <div className="auth-form-wrapper">
      <div className="auth-form-container">
        <h2 className="auth-form-title">{isLogin ? 'Вход в систему' : 'Регистрация'}</h2>
        {displayError && <div className="alert alert-danger">{displayError}</div>}
        {user ? (
          <div>
            <div className="alert alert-success">Вы вошли как {user.name}</div>
            <button onClick={handleLogout} className="btn btn-danger">
              Выйти
            </button>
          </div>
        ) : (
          <form onSubmit={handleSubmit} className="auth-form">
            <div className="mb-3">
              <label htmlFor="username" className="form-label">Имя пользователя:</label>
              <input
                type="text"
                className="form-control"
                id="username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
              />
            </div>
            <div className="mb-3">
              <label htmlFor="password" className="form-label">Пароль:</label>
              <input
                type="password"
                className="form-control"
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
            <button type="submit" className="gradient-button btn btn-primary" disabled={isLoading}>
              {isLoading ? 'Загрузка...' : isLogin ? 'Войти' : 'Зарегистрироваться'}
            </button>
          </form>
        )}
        {!user && (
          <button onClick={() => setIsLogin(!isLogin)} className="toggle-button btn btn-link">
            {isLogin ? 'Нет аккаунта? Зарегистрируйтесь' : 'Уже есть аккаунт? Войдите'}
          </button>
        )}
      </div>
    </div>
  );
};

export default AuthForm;
