import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../AuthContext/AuthContext';

const AuthForm = () => {
  const navigate = useNavigate();
  const [formType, setFormType] = useState('login');
  const [credentials, setCredentials] = useState({ username: '', password: '', email: '', newPassword: '' });
  const { login, register, isLoading, error, user, updatePassword } = useAuth();

  useEffect(() => {
    // После успешной аутентификации проверяем роль пользователя
    if (user && formType === 'login') {
      // Перенаправление в личный кабинет или панель администратора в зависимости от роли
      if(user.role === 'admin') {
        navigate('/adminPanel');
      } else {
        navigate('/userProfile');
      }
    }
  }, [user, formType, navigate]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCredentials(prevCredentials => ({ ...prevCredentials, [name]: value }));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (isLoading) return;

    try {
      if (formType === 'login') {
        await login(credentials.username, credentials.password);
        // Перенаправление будет обработано в useEffect
      } else if (formType === 'register') {
        const newUser = await register(credentials.username, credentials.password, credentials.email);
        alert('Вы успешно зарегистрированы на сайте!'); // Уведомление о успешной регистрации
        navigate('/userProfile'); // Переход в личный кабинет после регистрации
      } else if (formType === 'reset') {
        await updatePassword(credentials.username, credentials.newPassword);
      }
    } catch (error) {
      // Ошибка обрабатывается здесь, если что-то пойдет не так
    }
  };

  return (
    <div className="auth-form-wrapper">
      <div className="auth-form-container">
        <h2 className="auth-form-title">
          {formType === 'login' ? 'Вход в систему' : formType === 'register' ? 'Регистрация' : 'Сброс пароля'}
        </h2>
        <form onSubmit={handleSubmit}>
          {/* Форма регистрации */}
          {formType === 'register' && (
            <div className="form-group">
              <label htmlFor="email">Email</label>
              <input
                type="email"
                id="email"
                name="email"
                value={credentials.email}
                onChange={handleChange}
                required={formType === 'register'}
                disabled={isLoading}
              />
            </div>
          )}
          {/* Форма входа */}
          <div className="form-group">
            <label htmlFor="username">Имя пользователя</label>
            <input
              type="text"
              id="username"
              name="username"
              value={credentials.username}
              onChange={handleChange}
              required
              disabled={isLoading}
            />
          </div>
          {/* Ввод пароля */}
          {formType !== 'reset' && (
            <div className="form-group">
              <label htmlFor="password">Пароль</label>
              <input
                type="password"
                id="password"
                name="password"
                value={credentials.password}
                onChange={handleChange}
                required
                disabled={isLoading}
              />
            </div>
          )}
          {/* Сброс пароля */}
          {formType === 'reset' && (
            <div className="form-group">
              <label htmlFor="newPassword">Новый пароль</label>
              <input
                type="password"
                id="newPassword"
                name="newPassword"
                value={credentials.newPassword}
                onChange={handleChange}
                required
                disabled={isLoading}
              />
            </div>
          )}
          {/* Отображение ошибок */}
          {error && <div className="alert alert-danger" role="alert">{error}</div>}
          <button type="submit" className="gradient-button">
            {isLoading ? 'Загрузка...' : formType === 'login' ? 'Войти' : formType === 'register' ? 'Зарегистрироваться' : 'Сбросить пароль'}
          </button>
          {/* Переключение между формами */}
          <button type="button" className="toggle-button" onClick={() => setFormType(formType === 'login' ? 'register' : 'login')}>
            {formType === 'login' ? 'Зарегистрироваться' : 'Войти'}
          </button>
          {formType !== 'reset' && (
            <button type="button" className="toggle-button" onClick={() => setFormType('reset')}>
              Забыли пароль?
            </button>
          )}
        </form>
      </div>
    </div>
  );
};

export default AuthForm;
