import React, { createContext, useContext, useState, useEffect, useCallback } from 'react';

export const AuthContext = createContext(null);

export const useAuth = () => useContext(AuthContext);

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(localStorage.getItem('token'));
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  // Эффект для проверки аутентификации пользователя при загрузке компонента
  useEffect(() => {
    if (token) {
      fetchUserData();
    }
  }, [token]); // Передаем token в массив зависимостей, чтобы перезапустить эффект при его изменении

  const fetchUserData = useCallback(async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch('/api/users/me', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        // Если токен невалидный или произошла другая ошибка, очищаем данные аутентификации
        throw new Error('Ошибка аутентификации. Пожалуйста, войдите снова.');
      }

      const userData = await response.json();
      setUser(userData);
    } catch (error) {
      setError(error.message);
      setToken(null);
      localStorage.removeItem('token');
      setUser(null);
    } finally {
      setIsLoading(false);
    }
  }, [token]);

  const login = async (username, password) => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch('/api/users/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, password }),
      });

      const data = await response.json();
      if (!response.ok) {
        throw new Error(data.message || 'Произошла ошибка при аутентификации.');
      }

      setToken(data.token);
      localStorage.setItem('token', data.token);
      await fetchUserData();
    } catch (error) {
      setError(error.message);
      setUser(null);
    } finally {
      setIsLoading(false);
    }
  };

  const logout = () => {
    setUser(null);
    setToken(null);
    localStorage.removeItem('token');
    setError(null);
  };

  const authContextValue = {
    user,
    token,
    isLoading,
    error,
    login,
    logout,
    fetchUserData // Предоставляем эту функцию, если нужно будет вызвать ее из компонентов
  };

  return (
    <AuthContext.Provider value={authContextValue}>
      {children}
    </AuthContext.Provider>
  );
};
