import React, { useState } from 'react';
import '../Style/FurnitureConstructor.css'; // Импортируйте CSS стили

const FurnitureConstructor = () => {
  // Состояния для управления свойствами мебели
  const [dimensions, setDimensions] = useState({ width: 1660, height: 2780, depth: 240 });
  const [shelves, setShelves] = useState(7); // Пример для количества полок
  const [material, setMaterial] = useState('Бежевый, ЛДСП 16мм');

  // Функция для изменения размеров (пример для ширины)
  const handleDimensionChange = (dimension, value) => {
    setDimensions({ ...dimensions, [dimension]: value });
  };

  return (
    <div className="furniture-constructor">
      <h2>Конструктор мебели</h2>
      <div className="settings">
        {/* Ползунки для изменения размеров */}
        <div className="dimension-sliders">
          <div className="slider">
            <label>Ширина: {dimensions.width} мм</label>
            <input type="range" min="1000" max="2000" value={dimensions.width} onChange={(e) => handleDimensionChange('width', parseInt(e.target.value))} />
          </div>
          <div className="slider">
            <label>Высота: {dimensions.height} мм</label>
            <input type="range" min="2000" max="3000" value={dimensions.height} onChange={(e) => handleDimensionChange('height', parseInt(e.target.value))} />
          </div>
          <div className="slider">
            <label>Глубина: {dimensions.depth} мм</label>
            <input type="range" min="200" max="500" value={dimensions.depth} onChange={(e) => handleDimensionChange('depth', parseInt(e.target.value))} />
          </div>
        </div>

        {/* Выбор материала */}
        <div className="material-selector">
          <label>Материал:</label>
          <select value={material} onChange={(e) => setMaterial(e.target.value)}>
            <option value="Бежевый, ЛДСП 16мм">Бежевый, ЛДСП 16мм</option>
            {/* Добавьте другие варианты материалов здесь */}
          </select>
        </div>

        <div className="setting">
  <label>Количество полок:</label>
  <input type="number" min="0" max="10" value={shelves} onChange={(e) => setShelves(parseInt(e.target.value))} />
</div>

      </div>
    </div>
  );
};

export default FurnitureConstructor;
